<template>
    <div>
        <div class="image-container">
            <img ref="image" :src="src">
        </div>
        <img :src="destination" class= "image-preview">
        <label class="switch">
            <input type="checkbox" v-model="toggleSwitch" @change="handleToggleSwitchChange">
            <span class="slider"></span>
    </label>
    <span class="toggle-label">{{ toggleSwitch ? 'BW ON' : 'BW OFF' }}</span>
    <button class="download-button" @click="handleDownloadButtonClick">Download</button>
    </div>
</template>

<script>
    import Cropper from "cropperjs";
    function downloadURI(uri, name) {
        var link = document.createElement("a");
        link.download = name;
        link.href = uri;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
}
    export default{
        name: 'ImageCropper',
        props:{
            src: String,
            imageNumber: Number,
        },
        data(){
            return{
                toggleSwitch: false,
                cropper:{},
                destination:'data:image/jpeg;base64,',
                image:{},
                isLoading: false
            }
        },
        methods:{
            
            handleDownloadButtonClick() {
        // Llama a la funciÃ³n downloadURI con la URL de destino y un nombre para el archivo
                downloadURI(this.destination, 'downloaded_image.jpeg');
  }
        },
        mounted(){
            this.image = this.$refs.image;
            this.cropper=new Cropper(this.image,{
                scalable: false,
                aspectRatio: 1,
                cropend: async ()=>{
                    if (this.isLoading==false){
                        this.isLoading=true;
                        const cropData = this.cropper.getData();
                        console.log("Coordenadas de la zona de recorte:", cropData);
                        let payload = {
                            imageId: this.imageNumber,
                            position: {
                                x: cropData.x,
                                y: cropData.y
                                },
                            size: {
                                width: cropData.width,
                                height: cropData.height
                            },
                            grayscale: this.toggleSwitch
                        };
                        let request = await fetch('http://localhost:8000/image', {
                            method: 'POST',
                            headers: {
                                "Content-Type": "application/json"
                            },
                        body: JSON.stringify(payload)
                        });
                        let response = await request.json();

                        console.log("data:image/jpeg;base64,"+ response.result)
                        this.destination = "data:image/jpeg;base64,"+ response.result;
                        this.isLoading=false;
                    }
                }
            });
        }
    }
</script>

<style scoped>
    .image-container{
        margin-left: 300px;
        width: 280px;
        height: 480px;
        float:left;
    }
    .image-preview{
        width:300px;
        height:300px;
        float: left;
        margin-left: 10px;
    }
    .switch {
        position: relative;
        display: inline-block;
        width: 60px;
        height: 34px;
    }

    .switch input {
        opacity: 0;
        width: 0;
        height: 0;
    }

    .slider {
        position: absolute;
        cursor: pointer;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background-color: #ccc;
        -webkit-transition: 0.4s;
        transition: 0.4s;
        border-radius: 34px;
    }

    .slider:before {
        position: absolute;
        content: "";
        height: 26px;
        width: 26px;
        left: 4px;
        bottom: 4px;
        background-color: white;
        -webkit-transition: 0.4s;
        transition: 0.4s;
        border-radius: 50%;
    }

    input:checked + .slider {
        background-color: #2196f3;
    }

    input:focus + .slider {
        box-shadow: 0 0 1px #2196f3;
    }

    input:checked + .slider:before {
        -webkit-transform: translateX(26px);
        -ms-transform: translateX(26px);
        transform: translateX(26px);
    }
    .toggle-container {
        display: flex;
        align-items: center;
    }

    .toggle-label {
        margin-right: 100px;
        margin-left: 10px;
    }
    .download-button {
        background-color: #2196f3; 
        color: white;
        padding: 10px 15px;
        border: none;
        border-radius: 5px;
        margin-top: 300px; 
    }
</style>